import React, {Component} from 'react';
import './App.css';
// import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Accordion } from 'react-bootstrap'
import { Button, ButtonGroup, ButtonToolbar } from 'react-bootstrap-buttons';

// Be sure to include styles at some point, probably during your bootstraping
import 'react-bootstrap-buttons/dist/react-bootstrap-buttons.css';
import Modal from "react-bootstrap/Modal";
import { FormGroup, FormControl, FormLabel } from "react-bootstrap";


class App extends Component {

  state = {
    recipes: [
      {recipeName: 'Tea', ingredients: ['Tea leaves', 'Suger', 'Ginger', 'Water']},
      {recipeName: 'Coffee', ingredients: ['Coffee', 'Suger', 'Ginger', 'Water']},
      {recipeName: 'Milk Tea', ingredients: ['Tea leaves', 'Suger', 'Ginger', 'Milk']}
    ]
  }
  render(){
  const {recipes} = this.state;
  return (
    <div className="App container">
      <Accordion>
      {recipes.map((recipe, index)=>(
  <Card header={recipe.recipeName} eventkey = {index} key = {index}>
    <ol>
      {recipe.ingredients.map((item)=>(
        <li key={item}>{item}</li>
      ))}
    </ol>

  </Card>

      ))}
      </Accordion>

    </div>
  );
      }}

export default App;
